import Vue from 'vue';

const app = createApp(App)

Vue.use(Toasted, {
    iconPack: 'fontawesome',
    duration: 3000
})

Vue.toasted.register(
    'defaultSucess',
    payload => !payload.msg ? 'Operação realizada com seucesso!' : payload.msg,
    { type: 'secess', icon: 'check' }
)

Vue.toasted.register(
    'defaultError',
    payload => !payload.msg ? 'Oops.. Erro inesperado.' : payload.msg,
    { type: 'error', icon: 'times' }
)
